-- Script para crear la base de datos Fulbito y sus tablas
CREATE DATABASE Fulbito;
--  quiero un script que genere una base de datos que se llame "Fulbito"
--  una tabla llamada "equipos", con los siguientes atributos ("id", "nombre", "descripcion", "ciudad")
--  una tabla llamada "jugadores", con los siguientes atributos ("id", "nombre", "apodo", "descripcion", "foto")   
--  una tabla llamada "equipos_jugadores", con los siguientes atributos ("id", "equipo_id", "jugador_id", "fecha_inicio", "fecha_fin")

-- Tabla equipos
USE Fulbito;
CREATE TABLE equipos (
	id INT NOT NULL AUTO_INCREMENT,
	nombre VARCHAR(100) NOT NULL,
	descripcion VARCHAR(400),
	ciudad VARCHAR(100),
	PRIMARY KEY (id)
);

-- Tabla jugadores
CREATE TABLE jugadores (
	id INT NOT NULL AUTO_INCREMENT,
	nombre VARCHAR(100) NOT NULL,
	apodo VARCHAR(50),
	descripcion TEXT,
	foto TEXT,
	PRIMARY KEY (id)
);

-- Tabla equipos_jugadores
CREATE TABLE equipos_jugadores (
	id INT NOT NULL AUTO_INCREMENT,
	equipo_id INT NOT NULL,
	jugador_id INT NOT NULL,
	fecha_inicio DATE,
	fecha_fin DATE,
	PRIMARY KEY (id),
	FOREIGN KEY (equipo_id) REFERENCES equipos(id),
	FOREIGN KEY (jugador_id) REFERENCES jugadores(id)
);

